-- Create "orders" table
CREATE TABLE "orders" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "wallet_address" character varying NOT NULL, "price_in_cents" bigint NOT NULL, "cc_checkout_id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "modified_at" timestamptz NOT NULL, "payment_reference" character varying NULL, "payment_wallet_address" character varying NULL, "token_id" bigint NULL, "event_id" bigint NOT NULL, "ticket_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "orders_events_orders" FOREIGN KEY ("event_id") REFERENCES "events" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "orders_tickets_orders" FOREIGN KEY ("ticket_id") REFERENCES "tickets" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
