","keccak256":"0xb1970fac7b64e6c09611e6691791e848d5e3fe410fa5899e7df2e0afd77a99e3","license":"MIT"},"contracts/Event.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol"; 
import "./IEventFactory.sol";
import "./Ticket.sol";

contract Event is Initializable, OwnableUpgradeable {
    address[] public eventTickets;
    address ticketImplementation;
    address public factoryAddress; 
    mapping(address => address) public eventMinters;

    event TicketCreated(string ticketHash, address clone);

    function doesTicketExist(address ticketAddress) public view returns (bool) {
        for (uint i = 0; i < eventTickets.length; i++) {
            if (eventTickets[i] == ticketAddress) {
                return true;
            }
        }

        return false;
    }

    function initialize(
        address _ticketFactory,
        address owner,
        address _factoryAddress
    ) public initializer {
        __Ownable_init(owner);
        ticketImplementation = _ticketFactory;
        factoryAddress = _factoryAddress;
    }

    function createTicket(
        string memory name,
        uint256 maxSupply,
        uint256 mintPrice,
        string memory ticketHash 
    ) public onlyOwner returns (address) {
        address clone = Clones.clone(ticketImplementation);
        Ticket(clone).initialize(name, "TNFT", maxSupply, mintPrice, owner());
        eventTickets.push(clone);
        emit TicketCreated(ticketHash, clone);
        return clone;
    }

    function registerTicket(address ticketAddress) external {
        require(msg.sender == factoryAddress, "Caller is not the factory");
        eventTickets.push(ticketAddress);
    }

    function mintNFT(
        address ticketAddress,
        string memory tokenURI
    ) public payable returns (uint256) {
        require(doesTicketExist(ticketAddress), "Ticket doesn't belong to Event");
        Ticket ticket = Ticket(ticketAddress);
        require(msg.value >= ticket.mintPrice(), "Insufficient ETH sent");

        // Refund excess payment
        if (msg.value > ticket.mintPrice()) {
            payable(msg.sender).transfer(msg.value - ticket.mintPrice());
        }

        uint256 tokenId = ticket.mintNFT(msg.sender, tokenURI);
        eventMinters[msg.sender] = ticketAddress;

        IEventFactory(factoryAddress).recordMint(tokenId, ticketAddress, address(this), msg.sender);

        return tokenId;
    }

    function withdraw() public onlyOwner {
        uint256 balance = address(this).balance;
        payable(owner()).transfer(balance);
    }
}
","keccak256":"0x24807d5b454749a343a1a6bb16d1495dbb4cbc532dbf3981dabfe7031c4ab24d","license":"MIT"},"contracts/EventFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/proxy/Clones.sol"; 
import "./Event.sol";

contract EventFactory {
    address public immutable eventImplementation;
    address public immutable ticketImplementation;
    address[] public deployedEvents;              

    event EventCreated(string eventHash, address clone);
    event TicketMinted(
        uint256 indexed tokenId,
        address indexed ticketAddress,
        address indexed eventAddress,
        uint256 blockNumber,
        address minter
    );
    event TicketCreated(string ticketHash, address clone);

    struct AttendeeTicket {
        uint256 tokenId;
        address ticketAddress;
        address eventAddress;
    }

    mapping(address => mapping(address => uint256[])) public attendeeToTickets;
    mapping(uint256 => address) public ticketToAttendee;
    mapping(address => AttendeeTicket[]) public attendeeToAllTickets;

    constructor(
        address _eventImplementation,
        address _ticketImplementation 
    ) {
        eventImplementation = _eventImplementation;
        ticketImplementation = _ticketImplementation;
    }

    function createEvent(
        string memory eventHash
    ) external returns (address) {
        address eventClone = Clones.clone(eventImplementation);
        Event(eventClone).initialize(ticketImplementation, msg.sender, address(this));
        deployedEvents.push(eventClone);
        emit EventCreated(eventHash, eventClone);
        return eventClone;
    }

    function createTicket(
        address eventAddress,
        string memory name,
        uint256 maxSupply,
        uint256 mintPrice,
        string memory ticketHash
    ) external returns (address) {
        require(eventAddress != address(0), "Invalid event address");

        require(Event(eventAddress).owner() == msg.sender, "Caller is not the event owner");

        address ticketClone = Clones.clone(ticketImplementation);
        Ticket(ticketClone).initialize(name, "TNFT", maxSupply, mintPrice, msg.sender);

        Event(eventAddress).registerTicket(ticketClone);

        emit TicketCreated(ticketHash, ticketClone);
        return ticketClone;
    }

    function getDeployedEvents() external view returns (address[] memory) {
        return deployedEvents;
    }

    function recordMint(
        uint256 tokenId,
        address ticketAddress,
        address eventAddress,
        address minter
    ) external {
        emit TicketMinted(tokenId, ticketAddress, eventAddress, block.number, minter);

        ticketToAttendee[tokenId] = minter;

        attendeeToTickets[minter][eventAddress].push(tokenId);

        attendeeToAllTickets[minter].push(AttendeeTicket(tokenId, ticketAddress, eventAddress));
    }

    function getAttendeeTicketsForEvent(address attendee, address eventAddress) external view returns (uint256[] memory) {
        return attendeeToTickets[attendee][eventAddress];
    }

    function getTicketOwner(uint256 tokenId) external view returns (address) {
        return ticketToAttendee[tokenId];
    }

    function getTicketsOfAttendee(address attendee) external view returns (AttendeeTicket[] memory) {
        return attendeeToAllTickets[attendee];
    }

}
